/** @jsx jsx */
/** @jsxRuntime classic */
import { css, jsx } from "@emotion/core";
import { useContext } from "react";
import { MovieContext } from "../../Context/MovieContext";
import Container from "../ReusableComponents/Container";
import Popular from "../Popular/Popular";
import Movies from "../Movies/Movies";
import MoviesButton from "../Movies/MoviesButton";

const Output = () => {
    const {activeLink} = useContext(MovieContext);

/* WHEN POPULAR IS CLICKED GETS POPULAR MOVIES FROM API USING FUCNTION IN Popular.js */
    return(
        <div css={styles} className="output">
            {activeLink === "Popular" && (
                <Container>
                  <div className="Infinity"><h4>Avengers Infinity War</h4></div>
                    <Popular />
                </Container>
            )}
            {activeLink === "All Movies" && (
                <Container>
                    <Movies />
                    <MoviesButton />
                </Container>
            )}
        </div>
    );
};

const styles = css`
  width: 100%;
  min-height: calc(100vh - 80px);
  display: flex;
  .Infinity{
    margin-left: 1100px;
  }
  > .container {
    display: flex;
    flex-direction: column;
    justify-content: space-evenly;
  }
  @media (max-width: 1365px) {
    > .container {
      max-width: 90%;
    }
  }
`;

export default Output;
