/** @jsx jsx */
/** @jsxRuntime classic */
import { css, jsx } from "@emotion/core";
import { useContext } from 'react';
import { MovieContext } from '../../Context/MovieContext';
import Container from '../ReusableComponents/Container';
import HeroNavLogo from './HeroNavLogo';
import HeroNavMenu from './HeroNavMenu';
import HeroNavSearch from './HeroNavSearch';


const HeroNav = () => {
    const { hiddenMenu, setHiddenMenu } = useContext(MovieContext);

    return(
        <nav css={styles}>
            <Container>
                <div className="wrapper">
                    <HeroNavLogo />
                    <HeroNavMenu />
                </div>
                <HeroNavSearch />
                <i 
                 onClick={() => setHiddenMenu(!hiddenMenu)}
                 id="burgerMenu" 
                 className={hiddenMenu ? "fas fs-bars" : "fas fa-times"}
                 ></i>
            </Container>
        </nav>
  
    );
};

const styles = css`
  width: 100%;
  min-height: 80px;
  padding: 20px 0;
  .container {
    display: flex;
    align-items: center;
    justify-content: space-between;
    .wrapper {
      display: flex;
      align-items: center;
    }
    #burgerMenu {
      color: #f9a5ff;
      cursor: pointer;
      display: none;
    }
  }
  @media (max-width: 860px) {
    .container {
      #burgerMenu {
        display: block;
      }
    }
  }
  @media (max-width: 1365px) {
    .container {
      max-width: 90%;
    }
  }
`;

export default HeroNav;
