/** @jsx jsx */
/** @jsxRuntime classic */
import { css, jsx } from "@emotion/core";
import HeroNav from "./HeroNav";
import Output from "../Output/Output";


const Hero = () => {
    return(
        <body className="PopularBody">
        <section css={styles} className="hero">
            <HeroNav />
        </section>
        </body>
    );
};

const styles = css`
    width: 100%;
    min-height: 100vh;
    h1{
        padding-left: 220px;
        padding-top: 260px;
        font-size: 40px;
        letter-spacing: 0 10px;
    }
    .homeBtn{
        border: none;
        outline: none;
        border-radius: 50px;
        border: 2px solid #5F1B91;
        background: transparent;
        padding: 10px 16px;
        margin-top: 50px;
        margin-left: 220px;
        width: 190px;
        color: #ffffff;
        &::placeholder{
            color: #ffffff;
            letter-spacing: 1px;
        }
    }
`;

export default Hero;
