/** @jsx jsx */
/** @jsxRuntime classic */
import { css, jsx } from "@emotion/core";
import HeroNav from "./HeroNav";
import Output from "../Output/Output";


// Popular and All Movies Page


// Puts the Navbar and the Output of the Movies onto the Popular and All Movies Page.

const Hero = () => {
    return(
    <body className="PopularBody">
        <section css={styles} className="hero">
            <HeroNav />
            <Output />
        </section>
    </body>
    );
};

// Styles 

const styles = css`
    width: 100%;
    min-height: 100vh;
    h3{
        font-size: 30px;
        padding-left: 300px;
        margin-top: 50px;
        margin-bottom: 10px;

    }
`;

export default Hero;
