/** @jsx jsx */
/** @jsxRuntime classic */

import { css, jsx } from "@emotion/core";
import { useContext } from 'react';
import { MovieContext } from "../../Context/MovieContext";



const styles = css`
    border: none;
    outline: none;
    background: transparent;
    font-size: 22px;
    margin-right: 24px;
    font-weight: 800;
    cursor: pointer;
    user-select: none;
    @media (max-width: 860px) {
        font-size: 40px;
    }
`;

export default HeroNavLink;
