/** @jsx jsx */
/** @jsxRuntime classic */
import { css, jsx } from "@emotion/core";
import { useContext } from "react";
import { MovieContext } from "../../Context/MovieContext";

// function to allow the search bar to go onto only the All Movies page and not the Popular and Other Pages 

const HeroNavSearch = () => {
    const {search, setSearch, handleSearch, activeLink} = useContext(MovieContext);

    return (
        <form css={styles} onSubmit={handleSearch}>
          {activeLink !== "Popular" && (
            <input
              type="text"
              placeholder="Search for movies..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
          )}
        </form>
      );
    };

    // styles for search button

const styles = css`
    height: 40px;
    min-height: 40px;
    padding-left: 400px;
    input{
        border: none;
        padding: 10px 16px;
        width: 260px;
        color: #ffffff;
        &::placeholder{
            color: #ffffff;
            letter-spacing: 1px;
        }
    }
    @media(max-width: 860px){
        input{
            width: 220px;
        }
    }
`;


export default HeroNavSearch;
